INSERT [KS_Announce] ([ID], [ChannelID], [Title], [Content], [Author], [AddDate], [Newest], [ProvinceID], [CityID], [CountyID]) VALUES (81, 9999, N'KesionICMS系列产品正式推出！敬请关注！！！', N'<p>&nbsp; &nbsp;<span style="font-family: 微软雅黑">Kesion<sup>TM</sup>旗下的ICMS、IMALL、IEXAM是科兴公司拥有产权自主研发的基于WEB应用的B/S架构的管理系统，主要面向大中型站点，为大中型站点提供最佳保障，以最大化满足客户目前及今后的应用需求。该系统运行于微软公司开发的.NET2.0平台，汇集科兴多年的开发和服务经验，以创新理念和领先的技术打破了以往传统CMS系统的局限性，众多独创或领先的新特性和功能设计(如：创新开发的Xpower模板引擎、引入内容模型和节点的网站构架、丰富并且完全开放的可以个性化修改的标签库、多源数据整合支持、基于标准RBAC模型设计的权限管理、高度自由的流程管理、互动评论系统、无刷新的AJAX.NET技术等)，让用户能深刻体验到科兴在CMS领域以原创研发、服务客户为主导开发理念的独到之处和领先优势。</span></p><p>&nbsp; &nbsp;
	&nbsp;</p><p><br/></p>', N'任我行', CAST(0x0000A24600C589EC AS DateTime), 1, NULL, NULL, NULL)
