INSERT [KS_U_Photo] ([InfoID], [Title], [ClassID], [Content], [ShowStyle], [PageNum], [Details], [ChannelID], [KeyTags], [Intro], [TitleFontColor], [Attribute], [DefaultPic], [TurnUrl], [priority], [Hits], [HitsByDay], [HitsByWeek], [HitsByMonth], [LastHitsTime], [AddDate], [VoteID], [TemplateFile], [Template3GFile], [ShowON3G], [FileName], [RefreshTF], [Verify], [DelTF], [Inputer], [AttachmentCharge], [AttachmentChargeOnce], [SocialIDs], [ProvinceId], [CityID], [CountyID], [SEOTitle], [SEOKeyWords], [SEODescription], [CommentNum], [CopyFrom], [Author], [InfoPurview], [ArrGroupID], [ReadPoint], [ChargeType], [PitchTime], [ReadTimes], [DividePercent], [PKID]) VALUES (3632, CONVERT(TEXT, N'范斯沃斯住宅'), 837, CONVERT(TEXT, N'|http://i.kesion.com/UploadFiles/2013-11/74/b11302990255858415756N1.jpg|http://i.kesion.com/UploadFiles/2013-11/74/b1130299025587872825K6M.jpg||||http://i.kesion.com/UploadFiles/2013-11/74/b1130299025588497825TF5.jpg|http://i.kesion.com/UploadFiles/2013-11/74/b1130299025600529075DEG.jpg||||http://i.kesion.com/UploadFiles/2013-11/74/b1130299025601310325ZNX.jpg|http://i.kesion.com/UploadFiles/2013-11/74/b1130299025624279075DRH.jpg||||http://i.kesion.com/UploadFiles/2013-11/74/b11302990256252165754PF.jpg|http://i.kesion.com/UploadFiles/2013-11/74/b1130299025625685325HZQ.jpg'), 0, 12, CONVERT(TEXT, N'<p style="text-indent: 21pt;">
	<span style="font-family: 宋体;">范斯沃斯住宅是密斯</span><span>1945</span><span style="font-family: 宋体;">年为美国单身女医师范斯沃斯设计的一栋住宅，</span><span>1950</span><span style="font-family: 宋体;">年落成。住宅坐落在帕拉诺南部的福克斯河右岸，房子四周是一片平坦的牧野，夹杂着丛生茂密的树林。与其他住宅建筑不同的是，范斯沃斯住宅以大片的玻璃取代了阻隔视线的墙面，成为名副其实的&ldquo;看得见风景的房间&rdquo;。</span> <span style="font-family: 宋体;">　　</span></p>
<p style="text-indent: 21pt;">
	<span style="font-family: 宋体;">范斯沃斯住宅造型类似于一个架空的四边透明的盒子，建筑外观也简洁明净，高雅别致。袒露于外部的钢结构均被漆成白色，与周围的树木草坪相映成趣。由于玻璃墙面的全透明观感，建筑视野开阔，空间构成与周围风景环境一气呵成。</span> <span style="font-family: 宋体;">　　</span></p>
<p style="text-indent: 21pt;">
	<span style="font-family: 宋体;">不过，这栋全玻璃的房子更多是密斯建筑理念的一种实验性产品，在居住者便利方面则相对弱化。密斯认为这种透明的方式使得住宅的空间与空气得以自由流动，而在居住者看来，这无疑是让居住成为一种公众性、缺乏隐私的行为。这也是范斯沃斯住宅之所以备受争议的根本原因。</span></p>
'), 2, NULL, N'
