INSERT [KS_U_Article] ([InfoID], [Title], [ClassID], [Content], [PageTitle], [ChannelID], [KeyTags], [Intro], [TitleFontColor], [Attribute], [DefaultPic], [TurnUrl], [priority], [Hits], [HitsByDay], [HitsByWeek], [HitsByMonth], [LastHitsTime], [AddDate], [VoteID], [TemplateFile], [Template3GFile], [ShowON3G], [FileName], [RefreshTF], [Verify], [DelTF], [Inputer], [AttachmentCharge], [AttachmentChargeOnce], [SocialIDs], [ProvinceId], [CityID], [CountyID], [SEOTitle], [SEOKeyWords], [SEODescription], [CommentNum], [CopyFrom], [Author], [InfoPurview], [ArrGroupID], [ReadPoint], [ChargeType], [PitchTime], [ReadTimes], [DividePercent], [PKID]) VALUES (3704, CONVERT(TEXT, N'辽宁抚顺县旅游局就“官网只是一张图”道歉'), 1105, CONVERT(TEXT, N'<p style="text-align: center;"><img alt="辽宁抚顺县旅游局就“官网只是一张图”道歉" src="/UploadFiles/2015-3/75/b1130718975776542672SWC.jpg"/></p><p style="text-align: center; font-family: 宋体; font-size: 10pt;">近日网曝辽宁抚顺县旅游局网站仅是一张静态图片，网友调侃其为“年度最牛政府网站”。</p><p style="text-indent: 2em;">本报沈阳12月12日电 （记者何勇）近日，辽宁省抚顺县旅游局网址在网上热传，整个网站仅有一张静态图片，所有链接无法打开。网友纷纷调侃抚顺县旅游局网站为“年度最牛政府网站”。</p><p style="text-indent: 2em;">抚顺县旅游局在给本报的回应中称，该县旅游网为抚顺市旅游网二级网站，由市旅游局和抚顺市旅游网（抚顺市任天行网络科技有限公司）统一管理，抚顺县旅游局提供信息，自2011年7月1日起正式运行。</p><p style="text-indent: 2em;">2013年11月4日，抚顺市旅游局收到市公安局网络警察支队的整改通知书：网站安全技术保护措施不完善，存在重大安全隐患，要求对隐患进行限期整改。整改后，网站运行仍不稳定，需进一步调试。为防止界面“无法连接打不开”的负面影响，运营商在整改期间，将原网站首页截图放到界面上，引起网友误会和热评。</p><p style="text-indent: 2em;">抚顺县旅游局表示：“没有及时发现不妥，是工作的疏忽，对此深表歉意。”12日晚，记者输入“辽宁省抚顺县旅游网”，发现页面已经打开。</p>'), CONVERT(TEXT, N''), 1, N'', N'近日网曝辽宁抚顺县旅游局网站仅是一张静态图片，网友调侃其为“年度最牛政府网站”。本报沈阳12月12日电 （记者何勇）近日，辽宁省抚顺县旅游局网址在网上热传，整个网站仅有一张静态图片，所有链接无法打开。网友纷纷调侃抚顺县旅游局网站为“年度最牛政府网站”。抚顺县', CONVERT(TEXT, N''), CONVERT(TEXT, N'0000001'), CONVERT(TEXT, N'http://i.kesion.com/UploadFiles/2013-12/74/b1130317374373310490ICH.jpg'), CONVERT(TEXT, N''), 0, 0, 0, 0, 0, CAST(0x0000A29700F846FC AS DateTime), CAST(0x0000A293003DCC50 AS DateTime), 0, CONVERT(TEXT, N'{$TemplateDir}/文章系统/内容页.html'), CONVERT(TEXT, N'{$TemplateDir}/3G/Article/show.html'), 1, C